    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:

        if ("".equals(monthtxt.getText())) {
            JOptionPane.showMessageDialog(this, "Require field month");
            return;
        }

        if ("".equals(datetxt.getText())) {
            JOptionPane.showMessageDialog(this, "Require field date");
            return;
        }

        if ("".equals(yeartxt.getText())) {
            JOptionPane.showMessageDialog(this, "Require field year ");
            return;
        }

        String month = monthtxt.getText();
        int date = Integer.parseInt(datetxt.getText());
        int year = Integer.parseInt(yeartxt.getText());

        if ("".equals(nextMonth(month)) || "E".equals(nextMonth(month))) {
            JOptionPane.showMessageDialog(this, "Not found month");
            return;
        }

        if (year < 1812 || year > 2012) {
            JOptionPane.showMessageDialog(this, "Error: Year out of range (1812-2012)");
            return;
        }
        if (date < 1 || date > 31) {

            JOptionPane.showMessageDialog(this, "Error: Invalid date for month " + month);
            return;
        }
        if (month.equals("February")) {
            if (date > 29) {
                JOptionPane.showMessageDialog(this, "Error: Invalid date for February in a non-leap year");
                return;
            }
            if (date == 29 && !isLeapYear(year)) {
                date = 1;
                month = "March";
            } else if (date == 29 && isLeapYear(year)) {
                date = 29;
            } else if (date == 28 && isLeapYear(year)) {
                date = 29;
            } else {
                date++;
            }
        } else if (month.equals("April") || month.equals("June") || month.equals("September") || month.equals("November")) {
            if (date == 30) {
                date = 1;
                month = nextMonth(month);
            } else {
                date++;
            }
        } else {
            if (date == 31) {
                date = 1;
                month = nextMonth(month);
                if (month.equals("January")) {
                    year++;
                }
            } else {
                date++;
            }
        }

        resultL.setText("Result: " + month + " " + date + ", " + year);
    }//GEN-LAST:event_jButton1ActionPerformed

