     private static boolean isLeapYear(int year) {
        if (year % 4 != 0) {
            return false;
        } else if (year % 100 != 0) {
            return true;
        } else if (year % 400 != 0) {
            return false;
        } else {
            return true;
        }
    }

    private static String nextMonth(String month) {
        switch (month) {
            case "January":
                return "February";
            case "February":
                return "March";
            case "March":
                return "April";
            case "April":
                return "May";
            case "May":
                return "June";
            case "June":
                return "July";
            case "July":
                return "August";
            case "August":
                return "September";
            case "September":
                return "October";
            case "October":
                return "November";
            case "November":
                return "December";
            case "December":
                return "January";
            default:
                return "E";
        }
    }

